pragma solidity >=0.4.23 <0.6.0;

import 'openzeppelin-solidity/contracts/ownership/Ownable.sol';

contract Bid is Ownable {
  address public tenderer;
  int public score;
  Observation[] public observations;
  Observation[] public scoreObservations;

  struct Observation {
    string plain;
    string hash;
    string resPlain;
    string resHash;
  }

  constructor(address _tenderer) public {
    tenderer = _tenderer;
  }

  modifier onlyTenderer() {
    require(msg.sender == tenderer);
    _;
  }

  function submitObservation(string memory _plain) public {
    observations.push(Observation({plain: _plain, hash: "", resPlain: "", resHash: ""}));
  }

  function submitObservation(string memory _plain, string memory _hash) public {
    observations.push(Observation({plain: _plain, hash: _hash, resPlain: "", resHash: ""}));
  }

  function getObservationsLength() public view returns(uint) {
    return observations.length;
  }

  function respondObservation(uint _idx, string memory _plain, string memory _hash) public onlyOwner {
    Observation storage obs = observations[_idx];
    obs.resPlain = _plain;
    obs.resHash = _hash;
  }

  function submitScoreObservation(string memory _plain) public {
    scoreObservations.push(Observation({plain: _plain, hash: "", resPlain: "", resHash: ""}));
  }

  function submitScoreObservation(string memory _plain, string memory _hash) public {
    scoreObservations.push(Observation({plain: _plain, hash: _hash, resPlain: "", resHash: ""}));
  }

  function getScoreObservationsLength() public view returns(uint) {
    return scoreObservations.length;
  }

  function respondScoreObservation(uint _idx, string memory _plain, string memory _hash) public onlyTenderer {
    Observation storage obs = scoreObservations[_idx];
    obs.resPlain = _plain;
    obs.resHash = _hash;
  }

  function setScore(int _score) public onlyTenderer {
    score = _score;
  }
}
